pragma solidity ^0.8.0;

contract ERC20TokenCreater{
    mapping(address => address[]) tokenCreatedBy;
    mapping(address => uint256) noOfTokensCreated; 
    constructor(){}

    function ERC20TokenCreator(
        string memory name, 
        string memory symbol, 
        uint256 totalSupply, 
        uint8 decimals)
        external
        returns(address)
        {
            ERC20 token  = new ERC20(
                                name, 
                                symbol, 
                                totalSupply, 
                                decimals
                                );
            tokenCreatedBy[msg.sender][noOfTokensCreated[msg.sender]] = address(token);
            noOfTokensCreated[msg.sender] +=1;
            emit ERC20TokenCreated(address(token), msg.sender);
            return(address(token));
    }  

    event ERC20TokenCreated(address token, address creator);
}

